using Microsoft.VisualStudio.TestTools.UnitTesting;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;
using ACLibrary.Crypto.MixCryptSeries;

namespace TestREncryptLibs
{
    [TestClass]
    public class TestMixCrypt_D
    {
        [TestMethod]
        public void Test_Mid_EmptyString()
        {
            string oldEText = @"";

            Assert.AreEqual("", Mid.Instance.DecryptString(oldEText, ""));
        }

        [TestMethod]
        public void Test_Mid_SomeString_WPass()
        {
            string oldEText = @"";
            Assert.AreEqual("Hyper-V", Mid.Instance.DecryptString(oldEText, "112"));
        }
    }
}
